console.log('Rusty Proxy Debug Console Loaded');
window.rustyProxy = {
    version: '0.1.0',
    debug: function(msg) {
        console.log('[RUSTY-PROXY]', msg);
    },
    getInfo: function() {
        return {
            userAgent: navigator.userAgent,
            url: window.location.href,
            timestamp: new Date().toISOString()
        };
    }
};
